export const cartReducer = (state, action) => {
    switch (action.type) {
      case "Added":
        if (state.cartList.some((item) => item.id === action.payload.id)) {
          const increasedQuantity = state.cartList.map((item) => {
            console.log(item.id);
            return item.id === action.payload.id
              ? { ...item, quantity: item.quantity + 1 }
              : item;
          });
          return {
            ...state,
            cartList: increasedQuantity,
            displayModal: true,
            modalContent: "Added to cart",
          };
        } else {
          return {
            ...state,
            cartList: [...state.cartList, action.payload],
            displayModal: true,
            modalContent: "Added to cart",
          };
        }
  
      case "Increment":
        const increasedQuantity = state.cartList.map((item) => {
          console.log(item.id);
          return item.id === action.payload.id
            ? { ...item, quantity: item.quantity + 1 }
            : item;
        });
        return {
          ...state,
          cartList: increasedQuantity,
          displayModal: true,
          modalContent: "Quantity Increased",
        };
  
      case "Decrement":
        if (action.payload.quantity > 1) {
          const decreasedQuantity = state.cartList.map((item) => {
            console.log(item.id);
            return item.id === action.payload.id
              ? { ...item, quantity: item.quantity - 1 }
              : item;
          });
          return {
            ...state,
            cartList: decreasedQuantity,
            displayModal: true,
            modalContent: "Quantity Decreased",
          };
        } else {
          const filteredCartList = state.cartList.filter(
            (product) => product.id !== action.payload.id
          );
          return {
            ...state,
            cartList: filteredCartList
          };
        }
  
      case "Remove":
        const newCartList = state.cartList.filter(
          (product) => product.id !== action.payload.id
        );
        return {
          ...state,
          cartList: newCartList,
          displayModal: true,
          modalContent: "Removed",
        };
  
      case "MoveToCart":
        if (state.cartList.some((item) => item.id === action.payload.id)) {
          const MovedItem = state.cartList.map((item) => {
            return item.id === action.payload.id
              ? { ...item, quantity: item.quantity + 1 }
              : item;
          });
          return {
            ...state,
            cartList: MovedItem
          };
        } else {
          return {
            ...state,
            cartList: [...state.cartList, action.payload]
          };
        }
      default:
    }
  };
