import React,{useEffect, useState} from "react";
import axios from "axios";
import { useNavigate,NavLink } from "react-router-dom";
import { useAuth } from "../../Contexter/AuthContext";

function userNameHolder(event){
	console.log(event.target.value);
}

export function Login() {
	const { isUserLoggedIn, setIsUserLoggedIn } = useAuth();
	const navigate = useNavigate();
	const usernameRef = React.useRef();
	const passwordRef = React.useRef();
	const [disbaler, setDisabler] = useState(false);
	
	/* useEffect(() => {
		(async function () {
			const userId = "60b4af4ee2cd0c0028a55988";
			const password = "12345";
		  const { data } = await axios.get(
			"https://ecommercedata.saurabhsharma11.repl.co/v1/userData/60b4af4ee2cd0c0028a55988",{userId:userId, password: password}
		  );
		  console.log("this is user data",data);
		})();
	  },[]); */

	async function handleSubmit() {
		console.log(usernameRef.current.value, passwordRef.current.value);
		const userId = usernameRef.current.value;
		const password = passwordRef.current.value;
		const { data,status } = await axios.post(
		"https://ecommercedata.saurabhsharma11.repl.co/v1/userData/60b4af4ee2cd0c0028a55988",{userId:userId, password: password}
		);
		console.log("this is user data",data);
		if(status === 200){
			setIsUserLoggedIn((isUserLoggedIn) => !isUserLoggedIn && setDisabler(true));
			isUserLoggedIn && setDisabler(false) || navigate("/");
		}
	};

	function SignUp(){
		navigate("/Register")
	}

	return (
		<>
			<h1> Chhabhi laaya kya? </h1>
			<label>	Username :- 
				<input ref={usernameRef} />
			</label>
			<br /><br />
			<label>
				Password :- 
				<input ref={passwordRef} />
			</label>
			<br />
			<button	onClick={handleSubmit}>
				{isUserLoggedIn ? "Logged In" : "Logged out"}
			</button>
			<div>
				<span onClick={SignUp}>SignUp?</span>
			</div>
		</>
	);
}
