import React,{useEffect} from "react";
import axios from "axios";
import { useNavigate,NavLink } from "react-router-dom";
import { useAuth } from "../../Contexter/AuthContext";

function userNameHolder(event){
	console.log(event.target.value);
}

export function Login() {
	const { isUserLoggedIn, setIsUserLoggedIn } = useAuth();
	const navigate = useNavigate();
	const usernameRef = React.useRef();
	const passwordRef = React.useRef();

	useEffect(() => {
		(async function () {
		  const { data } = await axios.get(
			"https://ecommercedata.saurabhsharma11.repl.co/v1/userData"
		  );
		  console.log("this is cart list items",data);
		})();
	  },[]);
	
	const handleSubmit = () => {
		console.log(usernameRef.current.value, passwordRef.current.value);
		setIsUserLoggedIn((isUserLoggedIn) => !isUserLoggedIn);
		isUserLoggedIn || navigate("/")
	};

	function SignUp(){
		navigate("/Register")
	}

	return (
		<>
			<h1> Chhabhi laaya kya? </h1>
			<label>	Username :- 
				<input ref={usernameRef} />
			</label>
			<br /><br />
			<label>
				Password :- 
				<input ref={passwordRef} />
			</label>
			<br />
			<button	onClick={handleSubmit}>
				{isUserLoggedIn ? "I am logged In" : "I am logged out"}
			</button>
			<div>
				<span onClick={SignUp}>SignUp?</span>
			</div>
		</>
	);
}
