import React,{useEffect, useState} from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export function Address() {
  const  { token,id } = JSON.parse(localStorage?.getItem("login")) || {};
  const navigate = useNavigate();
  const [userData, setUserData] = useState([]);
  
  //for fetching data
  axios.interceptors.request.use(
    config => {
      config.headers.authorization = token;
      return config;
    },
    error => {
      return Promise.reject(error);
    }
  )

  useEffect(() => {
    (async function () {
      try{
        const { data } = await axios.get(
          "https://ecommercedata.saurabhsharma11.repl.co/v1/userData",{userID:id}
        );
        console.log("this is user details",data.userData);
        setUserData(data.userData);
      }
      catch(err){
        if(err.status){
          return navigate('/login');
        }
      }
    })();
  },[token]);

  return (
    <>
      <h1><em> User Details</em></h1>
      <div>Name : {userData.name}</div>
      <div>User-ID : {userData._id}</div>
      <div>Email-ID: {userData.mail}</div>
      <div>Contact-No : {userData.number}</div>
    </>
  );
}
